import React, { useState, useEffect } from "react";
import { Navigate } from "react-router-dom"; // Navigate ekledik
import { Select, MenuItem, Typography } from '@mui/material'; 
import TextField from "@mui/material/TextField";
import { useDispatch, useSelector } from "react-redux";
import { fetchAuthMe, selectIsAuth, selectUserRole } from "../../redux/slices/auth";

import styles from "./Kabinet.module.scss";

export const Kabinet = () => {
  const dispatch = useDispatch();
  const isAuth = useSelector(selectIsAuth);
  const userData = useSelector((state) => state.auth.data);
  const [rol, setRole] = useState(""); // Kullanıcı rolünü depolamak için state

  useEffect(() => {
    dispatch(fetchAuthMe());
  }, []);

  if (!window.localStorage.getItem("token") && !isAuth) {
    return <Navigate to="/" />;
  }

  const roles = [
    { _id: 'student', rol: 'Student' },
    { _id: 'teacher', rol: 'Teacher' },
  ];

  const roleMenuItems = roles.map(item => {
    // Kullanıcı "student" ise ve rol "teacher" ise bu öğeyi gösterme
    if (rol === 'student' && item._id === 'teacher') return null;
    // Kullanıcı "teacher" ise ve rol "student" ise bu öğeyi gösterme
    if (rol === 'teacher' && item._id === 'student') return null;
    // Diğer durumlarda MenuItem oluştur
    return (
      <MenuItem key={item._id} value={item._id}>
        <Typography>{item.rol}</Typography>
      </MenuItem>
    );
  });

  return (
    <div className={styles.container}>
      {/* Kullanıcı verilerini gösterme */}
      {userData && (
        <div>
          <p>ID: {userData._id}</p>
            <TextField
              id="outlined-multiline-flexible"
              label="email"
              multiline
              placeholder="email"
              value={userData.email}
              sx={{ width: '20%', height: '150px' }}
            />
            <TextField
              id="outlined-multiline-flexible"
              label="fullName"
              multiline
              placeholder="fullName"
              value={userData.fullName}
              sx={{ width: '20%', height: '150px' }}
            />
            <Select value={rol} onChange={(e) => setRole(e.target.value)} fullWidth>
              {roleMenuItems}
            </Select>
          <p>User Role: {userData.userRole}</p>
        </div>
      )}
    </div>
  );
};
