import React from 'react';
import { Route, Navigate } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { selectUserRole } from '../../redux/slices/auth' ;// Kullanıcı rolünü seçen bir selector

const PrivateRoute = ({ component: Component, ...rest }) => {
  const userRole = useSelector(selectUserRole); // Redux store'dan kullanıcı rolünü al

  return (
    <Route
      {...rest}
      element={userRole === 'admin' ? <Component {...rest} /> : <Navigate to="/" />}
    />
  );
};

export default PrivateRoute;
