import React, { useState, useEffect } from "react";
import { Navigate } from "react-router-dom"; 
import Typography from '@mui/material/Typography';
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import { useDispatch, useSelector } from "react-redux";
import { fetchAuthMe, selectIsAuth } from "../../redux/slices/auth";
import Select from "@mui/material/Select";
import MenuItem from "@mui/material/MenuItem";
import { selectUserRole } from "../../redux/slices/auth"; // selectUserRole ekledik


import styles from "./Kabinet.module.scss";

export const Kabinet = () => {
  const dispatch = useDispatch();
  const isAuth = useSelector(selectIsAuth);
  const userData = useSelector((state) => state.auth.data);
  const [email, setEmail] = useState("");
  const [fullName, setFullName] = useState("");
  const userRole = useSelector(selectUserRole);


  const [teachers, setTeachers] = useState([])
  useEffect(()=>{
    axios.get("http://localhost:5555/teachers")
    .then(teachers=>setTeachers(teachers.data))
    .catch(err=>console.log(err))
  },[])

  useEffect(() => {
    dispatch(fetchAuthMe());
  }, []);

  useEffect(() => {
    if (userData) {
      setEmail(userData.email);
      setFullName(userData.fullName);
    }
  }, [userData]);

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handleFullNameChange = (event) => {
    setFullName(event.target.value);
  };

  if (!window.localStorage.getItem("token") && !isAuth) {
    return <Navigate to="/" />;
  }

  return (
    <div className={styles.container}>
      {userData && (
        <div>
          <Typography classes={{ root: styles.title }} variant="h6">
                ID: {userData._id}
          </Typography>

          <TextField
            id="outlined-multiline-flexible"
            label="email"
            multiline
            placeholder="email"
            value={email}
            onChange={handleEmailChange}
            sx={{ width: '20%', height: '50px' }}
          />          
          {
             userRole && userRole.toLowerCase() === "student" && 
             <>
              <Typography classes={{ root: styles.title }} variant="h6">
                Группа
              </Typography>
             <Select

                className={styles.field}
                sx={{ width: '20%', height: '55px' }}
              >
                <MenuItem value="Student">Student</MenuItem>
              </Select>
              </>
          }
          <Typography classes={{ root: styles.title }} variant="h6">
                User Role: {userData.userRole}
          </Typography>
          <Button type="submit" size="small" variant="contained">
          Обновить
          </Button>
        </div>
      )}
    </div>
  );
};
