export const Rasp = ({
  id,
  time,
  day,
  predmed,
  group,
  teacher,
  kurs,
  room,
  isLoading,
  isEditable,
}) => {
  const dispatch = useDispatch();
  if (isLoading) {
    return <RSkeleton />;
  }

  const onClickRemove = () => {
    if (window.confirm("Are you sure you want to delete post")) {
      dispatch(fetchRemoveRasp(id));
    }
  };

  return (
    <tr>
      <td>{time}</td>
      <td>{day}</td>
      <td>{predmed}</td>
      <td>{group}</td>
      <td>{teacher}</td>
      <td>{kurs}</td>
      <td>{room}</td>
      <td>
        {isEditable && (
          <div className={styles.editButtons}>
            <Link to={`/rasps/${id}/edit`}>
              <IconButton color="primary">
                <EditIcon />
              </IconButton>
            </Link>
            <IconButton onClick={onClickRemove} color="secondary">
              <DeleteIcon />
            </IconButton>
          </div>
        )}
      </td>
    </tr>
  );
};
