import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";

import { Rasp } from "../components/Rasp/index";
import { Time } from "../components/Time";

import { fetchRasps } from "../redux/slices/rasps";
import { fetchTimes } from "../redux/slices/times";

export const Home = () => {
  const dispatch = useDispatch();
  const userData = useSelector((state) => state.auth.data);
  const { rasps } = useSelector((state) => state.rasps);
  const { times } = useSelector((state) => state.times);

  const [selectedTab, setSelectedTab] = useState(0);
  const [isDataLoading, setIsDataLoading] = useState(true);

  useEffect(() => {
    dispatch(fetchRasps());
    dispatch(fetchTimes());
  }, []);

  useEffect(() => {
    if (rasps.status === "succeeded" && times.status === "succeeded") {
      setIsDataLoading(false);
    }
  }, [rasps.status, times.status]);

  const handleTabChange = (event, newValue) => {
    setSelectedTab(newValue);
  };

  return (
    <>
      <Tabs
        style={{ marginBottom: 15 }}
        value={selectedTab}
        onChange={handleTabChange}
        aria-label="basic tabs example"
      >
        <Tab label="Расписания" />
        <Tab label="Время" />
        <Tab label="Дни" />
      </Tabs>
      <div className="columns mt-5">
        <div>
          {isDataLoading ? (
            <div>Loading...</div>
          ) : (
            rasps.status === "succeeded" &&
            times.status === "succeeded" &&
            (selectedTab === 0 ? (
              rasps.items.map((obj, index) => (
                <Rasp
                  key={index}
                  id={obj._id}
                  time={obj.time}
                  day={obj.day}
                  predmed={obj.predmed}
                  group={obj.group}
                  teacher={obj.teacher}
                  kurs={obj.kurs}
                  room={obj.room}
                  isEditable={userData?._id === obj.user._id}
                />
              ))
            ) : selectedTab === 1 ? (
              times.items.map((obj, index) => (
                <Time
                  key={index}
                  id={obj._id}
                  time={obj.time}
                  isEditable={userData?._id === obj.user._id}
                />
              ))
            ) : selectedTab === 2 ? (
              rasps.items.map((obj, index) => (
                <Rasp
                  key={index}
                  id={obj._id}
                  time={obj.time}
                  day={obj.day}
                  predmed={obj.predmed}
                  group={obj.group}
                  teacher={obj.teacher}
                  kurs={obj.kurs}
                  room={obj.room}
                  isEditable={userData?._id === obj.user._id}
                />
              ))
            ) : null)
          )}
        </div>
      </div>
    </>
  );
};
