import React, { useState, useEffect } from "react";
import { Navigate } from "react-router-dom"; 
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import { useDispatch, useSelector } from "react-redux";
import { fetchAuthMe, selectIsAuth } from "../../redux/slices/auth";
import Typography from "@mui/material/Typography";
import Select from "@mui/material/Select";
import MenuItem from "@mui/material/MenuItem";
import { selectUserRole } from "../../redux/slices/auth"; // selectUserRole ekledik


import styles from "./Kabinet.module.scss";

export const Kabinet = () => {
  const dispatch = useDispatch();
  const isAuth = useSelector(selectIsAuth);
  const userData = useSelector((state) => state.auth.data);
  const [email, setEmail] = useState("");
  const [fullName, setFullName] = useState("");
  const userRole = useSelector(selectUserRole);

  useEffect(() => {
    dispatch(fetchAuthMe());
  }, []);

  useEffect(() => {
    if (userData) {
      setEmail(userData.email);
      setFullName(userData.fullName);
    }
  }, [userData]);

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handleFullNameChange = (event) => {
    setFullName(event.target.value);
  };

  if (!window.localStorage.getItem("token") && !isAuth) {
    return <Navigate to="/" />;
  }

  return (
    <div className={styles.container}>
      {userData && (
        <div>
          <p>ID: {userData._id}</p>
          <TextField
            id="outlined-multiline-flexible"
            label="email"
            multiline
            placeholder="email"
            value={email}
            onChange={handleEmailChange}
            sx={{ width: '20%', height: '50px' }}
          />
          <TextField
            id="outlined-multiline-flexible"
            label="email"
            multiline
            placeholder="email"
            value={email}
            onChange={handleEmailChange}
            sx={{ width: '20%', height: '50px' }}
          />
          {
             userRole && userRole.toLowerCase() === "student" && 
             <Select
                {...register("userRole", { required: "Select the role" })}
                className={styles.field}
                fullWidth
              >
          <MenuItem value="Student">Student</MenuItem>
          <MenuItem value="Teacher">Teacher</MenuItem>
        </Select>
          }

          <p>User Role: {userData.userRole}</p>
          <Button type="submit" size="small" variant="contained">
          Обновить
          </Button>
        </div>
      )}
    </div>
  );
};
