import React from "react";
import { useDispatch, useSelector } from "react-redux";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import Grid from "@mui/material/Grid";

import { Rasp } from "../components/Rasp/index";

import { fetchRasps } from "../redux/slices/rasps";

export const Home = () => {
  const dispatch = useDispatch();
  const userData = useSelector((state) => state.auth.data);
  const { rasps } = useSelector((state) => state.rasps);

  const isRaspsLoading = rasps.status === "loading";

  React.useEffect(() => {
    dispatch(fetchRasps());
  }, []);
  console.log(rasps);
  return (
    <>
      <Tabs
        style={{ marginBottom: 15 }}
        value={0}
        aria-label="basic tabs example"
      >
        <Tab label="Расписания" />
      </Tabs>
      <div>
        <div>
          {(isRaspsLoading ? [...Array(50)] : rasps.items).map((obj, index) =>
            isRaspsLoading ? (
              <Rasp key={index} isLoading={true} />
            ) : (
              <Rasp
                id={obj.id}
                title={obj.time}
                day={obj.day}
                predmed={obj.predmed}
                group={obj.group}
                teacher={obj.teacher}
                kurs={obj.kurs}
                room={obj.room}
                isEditable={userData?._id === obj.user._id}
              />
            )
          )}
        </div>
      </div>
    </>
  );
};
