import React, { useEffect, useState } from 'react';
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import Select, { SelectChangeEvent } from '@mui/material/Select';



import "easymde/dist/easymde.min.css";
import styles from "./AddPost.module.scss";
import { useSelector } from "react-redux";
import { selectIsAuth } from "../../redux/slices/auth";
import { Navigate, useNavigate, useParams } from "react-router-dom";
import axios from "../../axios";

export const AddRasp = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const isAuth = useSelector(selectIsAuth);
  const [isLoading, setLoading] = React.useState(false);
  const [time, setTime] = React.useState("");
  const [day, setDay] = React.useState("");
  const [group, setGroup] = React.useState("");
  const [kurs, setKurs] = React.useState("");
  const [predmed, setPredmed] = React.useState("");
  const [room, setRoom] = React.useState("");
  const [teacher, setTeacher] = React.useState("");

  const isEditing = Boolean(id);

  const [times, setTimes] = useState([])
  useEffect(()=>{
    axios.get("http://localhost:5555/times")
    .then(times=>setTimes(times.data))
    .catch(err=>console.log(err))
  },[])

  const [days, setDays] = useState([])
  useEffect(()=>{
    axios.get("http://localhost:5555/days")
    .then(days=>setDays(days.data))
    .catch(err=>console.log(err))
  },[])

  const fields = { time, day, group, kurs, predmed, room, teacher };
  const onSubmit = async () => {
    try {
      setLoading(true);

      const { data } = isEditing
        ? await axios.patch(`/rasps/${id}`, fields)
        : await axios.post("/rasps", fields);
      const _id = isEditing ? id : data._id;
      navigate(`/`);
    } catch (err) {
      console.warn(err);
      alert("Fail create raspisaniya");
    }
  };

  React.useEffect(() => {
    if (id) {
      axios.get(`/rasps/${id}`).then(({ data }) => {
        setTime(data.time);
        setDay(data.day);
        setGroup(data.group);
        setKurs(data.kurs);
        setPredmed(data.predmed);
        setRoom(data.room);
        setTeacher(data.teacher);
      });
    }
  }, []);

  if (!window.localStorage.getItem("token") && !isAuth) {
    return <Navigate to="/" />;
  }

  return (
    <>
    <div className="bg-white px-4 pt-3 pb-4 rounded-sm border border-gray-200 flex-1">
    <strong className="text-gray-700 font-medium">Groups</strong>
    <div className="border-x border-gray-200 rounded-sm mt-2">
      <table className="w-full text-gray-700">
      <thead>
            <tr>
              <th>Время</th>
              <th>День</th>
              <th>Предмет</th>
              <th>Группа</th>
              <th>Преподаватель</th>
              <th>Кабинет</th>
            </tr>
          </thead>
          <tbody>
              <td>
              <InputLabel id="demo-simple-select-label">Age</InputLabel>
              <Select
              labelId="demo-simple-select-label"
              id="demo-simple-select"
              value={age}
              label="Age"
              onChange={handleChange}
              >
              <MenuItem value={10}>Ten</MenuItem>
              <MenuItem value={20}>Twenty</MenuItem>
              <MenuItem value={30}>Thirty</MenuItem>
              </Select>
                  <select onChange={(e) => setTime(e.target.value)}>
                  <option value={time}></option>
                    {times.map(item => (
                      <option key={item._id} value={item._id}>{item.time}</option>
                    ))}
                  </select>
              </td>
              <td>
                <select onChange={(e) => setDay(e.target.value)}>
                  <option></option>
                {days.map(item => (
                  <option key={item._id} value={item._id}>{item.day}</option>
                ))}
              </select>
              </td>
          </tbody>
      </table>
    </div>
    </div>
      <TextField
        classes={{ root: styles.tags }}
        variant="standard"
        placeholder="group"
        value={group}
        onChange={(e) => setGroup(e.target.value)}
        fullWidth
      />
      <TextField
        classes={{ root: styles.tags }}
        variant="standard"
        placeholder="kurs"
        value={kurs}
        onChange={(e) => setKurs(e.target.value)}
        fullWidth
      />
      <TextField
        classes={{ root: styles.tags }}
        variant="standard"
        placeholder="predmed"
        value={predmed}
        onChange={(e) => setPredmed(e.target.value)}
        fullWidth
      />
      <TextField
        classes={{ root: styles.tags }}
        variant="standard"
        placeholder="room"
        value={room}
        onChange={(e) => setRoom(e.target.value)}
        fullWidth
      />
      <TextField
        classes={{ root: styles.tags }}
        variant="standard"
        placeholder="teacher"
        value={teacher}
        onChange={(e) => setTeacher(e.target.value)}
        fullWidth
      />

      <div className={styles.buttons}>
        <Button onClick={onSubmit} size="large" variant="contained">
          {isEditing ? "Сохранить" : "Опубликовать"}
        </Button>
        <a href="/">
          <Button size="large">Отмена</Button>
        </a>
      </div>
      </>
  );
};
