import React, { useEffect, useState } from 'react';
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import Select from "@mui/material/Select";
import MenuItem from "@mui/material/MenuItem";
import Typography from '@mui/material/Typography';
import axios from "../../axios";

const AddRasp = () => {
  const [rows, setRows] = useState([{ time: "", day: "", group: "", kurs: "", predmet: "", room: "", teacher: "" }]);

  const addRow = () => {
    setRows([...rows, { time: "", day: "", group: "", kurs: "", predmet: "", room: "", teacher: "" }]);
  };

  const handleChange = (index, key, value) => {
    const newRows = [...rows];
    newRows[index][key] = value;
    setRows(newRows);
  };

  const handleSubmit = async () => {
    try {
      await axios.post("/rasps", rows);
      // Başarı mesajı veya diğer işlemler buraya eklenebilir
    } catch (err) {
      console.error("Error:", err);
      alert("Fail create raspisaniya");
    }
  };

  return (
    <>
      <div className="bg-white px-4 pt-3 pb-4 rounded-sm border border-gray-200 flex-1">
        <strong className="text-gray-700 font-medium"></strong>
        <div className="border-x border-gray-200 rounded-sm mt-2">
          <table className="w-full text-gray-700">
            <thead>
              <tr>
                <th>Группа</th>
                <th>День</th>
                <th>Время</th>
                <th>Предмет</th>
                <th>Преподаватель</th>
                <th>Кабинет</th>
              </tr>
            </thead>
            <tbody>
              {rows.map((row, index) => (
                <tr key={index}>
                  <td>
                    <Select value={row.group} onChange={(e) => handleChange(index, "group", e.target.value)} fullWidth>
                      {/* Group options */}
                    </Select>
                  </td>
                  <td>
                    <Select value={row.day} onChange={(e) => handleChange(index, "day", e.target.value)} fullWidth>
                      {/* Day options */}
                    </Select>
                  </td>
                  <td>
                    <Select value={row.time} onChange={(e) => handleChange(index, "time", e.target.value)} fullWidth>
                      {/* Time options */}
                    </Select>
                  </td>
                  <td>
                    <Select value={row.predmet} onChange={(e) => handleChange(index, "predmet", e.target.value)} fullWidth>
                      {/* Predmet options */}
                    </Select>
                  </td>
                  <td>
                    <Select value={row.teacher} onChange={(e) => handleChange(index, "teacher", e.target.value)} fullWidth>
                      {/* Teacher options */}
                    </Select>
                  </td>
                  <td>
                    <TextField
                      variant="standard"
                      placeholder="room"
                      value={row.room}
                      onChange={(e) => handleChange(index, "room", e.target.value)}
                      fullWidth
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
      <div className="flex justify-end mt-4">
        <Button onClick={addRow} size="large" variant="outlined">Yeni Satır Ekle</Button>
        <Button onClick={handleSubmit} size="large" variant="contained" color="primary">Kaydet</Button>
      </div>
    </>
  );
};

export default AddRasp;
