          </thead>
          <tbody>
            {filteredRasps.map((item) => (
              <tr key={item._id}>
                <td>{item.time.time}</td>
                <td>{item.day.day}</td>
                <td>{item.predmet.predmet}</td>
                <td>{item.group.group}</td>
