import Container from "@mui/material/Container";
import { Routes, Route, Navigate } from "react-router-dom";

import React from "react";
import { Header } from "./components";
import { Home, Registration, Login, AddRasp, AddTime, AddPredmet } from "./pages";
import { useDispatch, useSelector } from "react-redux";
import { fetchAuthMe, selectIsAuth, selectUserRole } from "./redux/slices/auth"; // selectUserRole ekledik
import { AddGroup } from "./pages/AddGroup";
import { AddTeacher } from "./pages/AddTeacher";

function App() {
  const dispatch = useDispatch();
  const isAuth = useSelector(selectIsAuth);
  const userRole = useSelector(selectUserRole); // Kullanıcı rolünü alıyoruz

  React.useEffect(() => {
    dispatch(fetchAuthMe());
  }, []);

  // Sadece admin rolüne sahip kullanıcılar için erişim kontrolü
  const adminRoute = (component) => {
    if (!isAuth) {
      return <Navigate to="/login" />;
    }
    if (userRole !== "admin") {
      return <Navigate to="/" />;
    }
    return component;
  };

  return (
    <>
      <Header />
      <Container maxWidth="lg">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/add-rasp" element={adminRoute(<AddRasp />)} /> {/* Sadece admin erişebilir */}
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Registration />} />
          <Route path="/add-time" element={<AddTime />} />
          <Route path="/times/:id/edit" element={<AddTime />} />
          <Route path="/add-predmet" element={<AddPredmet />} />
          <Route path="/predmets/:id/edit" element={<AddPredmet />} />
          <Route path="/add-group" element={<AddGroup />} />
          <Route path="/groups/:id/edit" element={<AddGroup />} />
          <Route path="/add-teacher" element={<AddTeacher />} />
          <Route path="/teachers/:id/edit" element={<AddTeacher />} />
        </Routes>
      </Container>
    </>
  );
}

export default App;
