import React from "react";
import { useDispatch, useSelector } from "react-redux";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import Grid from "@mui/material/Grid";

import { Rasp } from "../components/Rasp";

import { fetchRasps } from "../redux/slices/posts";

export const Home = () => {
  const dispatch = useDispatch();
  const userData = useSelector((state) => state.auth.data);
  const { rasps } = useSelector((state) => state.posts);

  const isRaspsLoading = rasps.status === "loading";

  React.useEffect(() => {
    dispatch(fetchRasps());
  }, []);
  console.log(rasps);
  return (
    <>
      <Tabs
        style={{ marginBottom: 15 }}
        value={0}
        aria-label="basic tabs example"
      >
        <Tab label="Расписания" />
      </Tabs>
      <Grid container spacing={4}>
        <Grid xs={8} item>
          {(isRaspsLoading ? [...Array(5)] : rasps.items).map((obj, index) =>
            isRaspsLoading ? (
              <Post key={index} isLoading={true} />
            ) : (
              <Post
                id={obj._id}
                title={obj.time}
                day={obj.day}
                createdAt={obj.createdAt}
                viewsCount={obj.viewsCount}
                tags={obj.tags}
                isEditable={userData?._id === obj.user._id}
              />
            )
          )}
        </Grid>
      </Grid>
    </>
  );
};
