import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";

import { Rasp } from "../components/Rasp/index";
import { Time } from "../components/Time";

import { fetchRasps } from "../redux/slices/rasps";
import { fetchTimes } from "../redux/slices/times";
import { fetchDays } from "../redux/slices/days";
import { Day } from "../components/Day";

export const Home = () => {
  const dispatch = useDispatch();
  const userData = useSelector((state) => state.auth.data);
  const { rasps } = useSelector((state) => state.rasps);
  const { times } = useSelector((state) => state.times);
  const { days }  = useSelector((state) => state.days);

  const [selectedTab, setSelectedTab] = useState(0); // Durum değişikliği

  useEffect(() => {
    dispatch(fetchRasps());
    dispatch(fetchTimes());
    dispatch(fetchDays());
  }, []);

  // Rasps ve times yüklenene kadar bekleyen loading state
  const isRaspsLoading = rasps.status === "loading";
  const isTimesLoading = times.status === "loading";
  const isDaysLoading = days.status === "loading";

  const handleTabChange = (event, newValue) => {
    setSelectedTab(newValue);
  };

  return (
    <>
      <Tabs
        style={{ marginBottom: 15 }}
        value={selectedTab}
        onChange={handleTabChange}
        aria-label="basic tabs example"
      >
        <Tab label="Расписания" />
        <Tab label="Время" />
        <Tab label="Дни" />
      </Tabs>
      <div className="columns mt-5">
        <div>
          {selectedTab === 0 &&
            (isRaspsLoading ? (
              [...Array(50)].map((_, index) => (
                <Rasp key={index} isLoading={true} />
              ))
            ) : (
              rasps.items.map((obj) => (
                <Rasp
                  key={obj._id}
                  id={obj._id}
                  time={obj.time.time}
                  day={obj.day}
                  predmed={obj.predmed}
                  group={obj.group}
                  teacher={obj.teacher}
                  kurs={obj.kurs}
                  room={obj.room}
                  isEditable={userData?._id === obj.user._id}
                />
              ))
            ))}
          {selectedTab === 1 &&
            (isTimesLoading ? (
              <p>Loading...</p>
            ) : (
              times.items.map((time) => (
                <Time
                  key={time._id}
                  id={time._id}
                  time={time.time}
                  isEditable={userData?._id === time.user._id}
                />
              ))
            ))}
          {selectedTab === 2 &&
            (isDaysLoading ? (
              [...Array(50)].map((_, index) => (
                <Rasp key={index} isLoading={true} />
              ))
            ) : (
              days.items.map((obj) => (
                <Day
                  key={obj._id}
                  id={obj._id}
                  time={obj.time}
                  day={obj.day}
                  predmed={obj.predmed}
                  group={obj.group}
                  teacher={obj.teacher}
                  kurs={obj.kurs}
                  room={obj.room}
                  isEditable={userData?._id === obj.user._id}
                />
              ))
            ))}
        </div>
      </div>
    </>
  );
};
