import React from "react";
import { Link } from "react-router-dom";
import clsx from "clsx";
import IconButton from "@mui/material/IconButton";
import DeleteIcon from "@mui/icons-material/Clear";
import EditIcon from "@mui/icons-material/Edit";


import styles from "./Rasp.module.scss";
import { RSkeleton } from "./Skeleton";
import { useDispatch } from "react-redux";
import { fetchRemoveRasp } from "../../redux/slices/rasps";

export const Rasp = ({
  id,
  time,
  day,
  predmed,
  group,
  teacher,
  kurs,
  room,
  isLoading,
  isEditable,
}) => {
  const dispatch = useDispatch();
  if (isLoading) {
    return <RSkeleton />;
  }

  const onClickRemove = () => {
    if (window.confirm("Are you sure you want to delete post")) {
      dispatch(fetchRemoveRasp(id));
    }
  };

  return (

    <table>
            <thead>
              <tr>
                <th>Time</th>
                <th>Day</th>
                <th>Predmed</th>
                <th>Group</th>
                <th>Teacher</th>
                <th>Kurs</th>
                <th>Room</th>
              </tr>
            </thead>
          <tbody></tbody>
    <tr>
      <td>{time}</td>
      <td>{day}</td>
      <td>{predmed}</td>
      <td>{group}</td>
      <td>{teacher}</td>
      <td>{kurs}</td>
      <td>{room}</td>
      <td>
      <div className={clsx(styles.root)}>
      {isEditable && (
        <div>
          <Link to={`/rasps/${id}/edit`}>
            <IconButton color="primary">
              <EditIcon />
            </IconButton>
          </Link>
          <IconButton onClick={onClickRemove} color="secondary">
            <DeleteIcon />
          </IconButton>
        </div>
      )}
        </div>
      </td>
    </tr>
  </table>
  );
};
