import { configureStore } from "@reduxjs/toolkit";
import { raspsReducer } from "./slices/rasps";
import { authReducer } from "./slices/auth";

const store = configureStore({
  reducer: {
    posts: raspsReducer,
    auth: authReducer,
  },
});

export default store;
