import React, { useState, useEffect } from "react";
import TextField from "@mui/material/TextField";
import MenuItem from "@mui/material/MenuItem";
import Paper from "@mui/material/Paper";
import Button from "@mui/material/Button";

import "easymde/dist/easymde.min.css";
import styles from "./AddPost.module.scss";
import { useSelector } from "react-redux";
import { selectIsAuth } from "../../redux/slices/auth";
import { Navigate, useNavigate, useParams } from "react-router-dom";
import axios from "../../axios";

export const AddRasp = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const isAuth = useSelector(selectIsAuth);
  const [isLoading, setLoading] = useState(false);
  const [timeOptions, setTimeOptions] = useState([]);
  const [selectedTime, setSelectedTime] = useState("");
  const [day, setDay] = useState("");
  const [group, setGroup] = useState("");
  const [kurs, setKurs] = useState("");
  const [predmed, setPredmed] = useState("");
  const [room, setRoom] = useState("");
  const [teacher, setTeacher] = useState("");

  const isEditing = Boolean(id);

  const onSubmit = async () => {
    try {
      setLoading(true);
      const fields = { time: selectedTime, day, group, kurs, predmed, room, teacher };
      const { data } = isEditing
        ? await axios.patch(`/rasps/${id}`, fields)
        : await axios.post("/rasps", fields);
      const _id = isEditing ? id : data._id;
      navigate(`/`);
    } catch (err) {
      console.warn(err);
      alert("Fail create raspisaniya");
    }
  };

  useEffect(() => {
    // Zaman verilerini veritabanından almak için bir API çağrısı yapabilirsiniz.
    // Örnek olarak sabit bir dizi kullanıyoruz.
    useEffect(()=>{
      axios.get("http://localhost:5555/times")
      .then(times=>setTimes(times.data))
      .catch(err=>console.log(err))
    },[])
    const zamanlar = ["09:00", "10:00", "11:00", "12:00"]; // Veritabanından alınan zaman verileri
    setTimeOptions(zamanlar);
  }, []);

  if (!window.localStorage.getItem("token") && !isAuth) {
    return <Navigate to="/" />;
  }

  return (
    <Paper style={{ padding: 30 }}>
      <TextField
        select
        variant="standard"
        label="Time"
        value={selectedTime}
        onChange={(e) => setSelectedTime(e.target.value)}
        fullWidth
      >
        {timeOptions.map((option) => (
          <MenuItem key={option} value={option}>
            {option}
          </MenuItem>
        ))}
      </TextField>
      <TextField
        classes={{ root: styles.tags }}
        variant="standard"
        placeholder="day"
        value={day}
        onChange={(e) => setDay(e.target.value)}
        fullWidth
      />
      <TextField
        classes={{ root: styles.tags }}
        variant="standard"
        placeholder="group"
        value={group}
        onChange={(e) => setGroup(e.target.value)}
        fullWidth
      />
      <TextField
        classes={{ root: styles.tags }}
        variant="standard"
        placeholder="kurs"
        value={kurs}
        onChange={(e) => setKurs(e.target.value)}
        fullWidth
      />
      <TextField
        classes={{ root: styles.tags }}
        variant="standard"
        placeholder="predmed"
        value={predmed}
        onChange={(e) => setPredmed(e.target.value)}
        fullWidth
      />
      <TextField
        classes={{ root: styles.tags }}
        variant="standard"
        placeholder="room"
        value={room}
        onChange={(e) => setRoom(e.target.value)}
        fullWidth
      />
      <TextField
        classes={{ root: styles.tags }}
        variant="standard"
        placeholder="teacher"
        value={teacher}
        onChange={(e) => setTeacher(e.target.value)}
        fullWidth
      />

      <div className={styles.buttons}>
        <Button onClick={onSubmit} size="large" variant="contained">
          {isEditing ? "Сохранить" : "Опубликовать"}
        </Button>
        <a href="/">
          <Button size="large">Отмена</Button>
        </a>
      </div>
    </Paper>
  );
};
