import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";

import { Rasp } from "../components/Rasp/index";
import { Time } from "../components/Time";

import { fetchRasps } from "../redux/slices/rasps";
import { fetchTimes } from "../redux/slices/times";

export const Home = () => {
  const dispatch = useDispatch();
  const userData = useSelector((state) => state.auth.data);
  const { rasps } = useSelector((state) => state.rasps);
  const { times } = useSelector((state)=>state.times);

  const [selectedTab, setSelectedTab] = useState(0); // Durum değişikliği

  const isRaspsLoading = rasps.status === "loading";
  const isTimesLoading = times.status === "loading";

  React.useEffect(() => {
    dispatch(fetchRasps());
  }, []);

  const handleTabChange = (event, newValue) => {
    setSelectedTab(newValue);
  };

  return (
    <>
      <Tabs
        style={{ marginBottom: 15 }}
        value={selectedTab}
        onChange={handleTabChange} // Tab değiştirme işleyicisi
        aria-label="basic tabs example"
      >
        <Tab label="Расписания" />
        <Tab label="Время" />
        <Tab label="Дни" />
      </Tabs>
      <div className="columns mt-5">
        <div>
          {(isRaspsLoading ? [...Array(50)] : rasps.items).map((obj, index) =>
            isRaspsLoading ? (
              <Rasp key={index} isLoading={true} />
            ) : (
              selectedTab === 0 ? ( // Seçilen taba göre içeriği filtreleme
                <Rasp
                  key={obj._id}
                  id={obj._id}
                  time={obj.time}
                  day={obj.day}
                  predmed={obj.predmed}
                  group={obj.group}
                  teacher={obj.teacher}
                  kurs={obj.kurs}
                  room={obj.room}
                  isEditable={userData?._id === obj.user._id}
                />
              ) : selectedTab === 1 ? (
                <Time
                  id={obj._id}
                  time={obj.time}
                  isEditable={userData?._id === obj.user._id}
                />
              ) : selectedTab === 2 ? (
                <Rasp
                  key={obj._id}
                  id={obj._id}
                  time={obj.time}
                  day={obj.day}
                  predmed={obj.predmed}
                  group={obj.group}
                  teacher={obj.teacher}
                  kurs={obj.kurs}
                  room={obj.room}
                  isEditable={userData?._id === obj.user._id}
                />
              ) : null
            )
          )}
        </div>
      </div>
    </>
  );
};
